//******************************************************************************
//*** Globals
//******************************************************************************

static const char *CommandExe;
static const char *TextLogFilename = NULL;
static int Port = 6969;
static bool RPCInitialized = false;
static bool NoExit  = false;
static char *OAOpt = NULL;

//******************************************************************************
//*** Callbacks
//******************************************************************************
oaCommandType GetNextCommand(void* user_data, 
                             oaCommand *command)
{
  oaiFunctionTable *func_table;

  func_table = (oaiFunctionTable *)user_data;
  assert(func_table->GetNextCommand);
  return func_table->GetNextCommand(command);
}

